mapper.select:

-- 수강신청 했는지 확인.
SELECT 
	COUNT(1) AS CNT 
FROM TB_APP_INFO 
WHERE 1=1
	AND GRCODE = #{grcode}
	AND GRSEQ = #{grseq}
	AND USERNO = #{userno}

mapper.update:

-- 수강 신청 정보 수정.
UPDATE TB_APP_INFO SET 
	${updateStr}
	DINSERT = 'Y',
	DEPT = #{dept}
WHERE GRCODE = #{grcode}
	AND GRSEQ = #{grseq}
	AND USERNO = #{userno}


-- 수강신청 기관 및 부서 정보수정.
UPDATE TB_APP_INFO SET 
	DEPT = #{dept},
	DEPTSUB = #{deptsub},
	JIK = #{jik}
WHERE GRCODE = #{grcode} 
	AND GRSEQ = #{grseq} 
	AND USERNO = #{userno}


-- 수강생의 승인/승인취소 및 선발고사 여부
UPDATE TB_APP_INFO SET 
	GRCHK = #{grchk},
	STARTEXAM_YN = #{startexamYn}
WHERE USERNO = #{userno} 
	AND GRCODE = #{grcode} 
	AND GRSEQ = #{grseq}
